import React from "react";

interface FilterModalProps {
  isOpen: boolean;
  onClose: () => void;
}

const FilterModal: React.FC<FilterModalProps> = ({ isOpen, onClose }) => {
  return (
    <div className={`fixed top-0 left-0 w-full h-full flex items-center justify-center z-10 ${isOpen ? "block" : "hidden"}`}>
      <div className="modal-overlay fixed top-0 left-0 w-full h-full bg-gray-600 opacity-50"></div>
      <div className="modal-container bg-white w-456 h-876 rounded-md shadow-md transform translate-x-full transition-transform z-20">
        <div className="p-4">
          {/* Your filter options and other content */}
          <button onClick={onClose}>Close</button>
        </div>
      </div>
    </div>
  );
};

export default FilterModal;
