import React from "react";
import close from "../assets/close.svg";

interface FilterModalProps {
  isOpen: boolean;
  onClose: () => void;
}

const FilterModal: React.FC<FilterModalProps> = ({ isOpen, onClose }) => {
  return (
    <div className={`fixed inset-0 flex items-center justify-center ${isOpen ? "" : "hidden"}`}>
      {/* Grayed-out background */}
      <div className="fixed inset-0 bg-gray-400 opacity-50 z-10" onClick={onClose}></div>

      {/* Filter modal with custom animation */}
      <div
        className={`fixed top-2 right-2 w-[420px] h-[670px] rounded-[20px] bg-white border border-gray-200 shadow-2xl transform z-20 animate-slide-in-slow ${
          isOpen ? "" : "translate-x-full"
        }`}
      >
        <div className="p-4 ">
          {/* Your filter options and other content */}
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-2xl font-[degularsemibold] text-neutral-900">Filter</h2>
            <button onClick={onClose} className="hover:bg-gray-200 rounded-full">
              <img src={close} alt="close" />
            </button>
          </div>
          <div className="flex gap-2 overflow-x-auto ">
            <button className="px-4 py-2.5 border border-gray-100 items-center rounded-[100px]">
              <p className="text-neutral-900 text-sm font-[degularsemibold] ">Today</p>
            </button>
            <button className="px-4 py-2.5 border border-gray-100 items-center rounded-[100px]">
              <p className="text-neutral-900 text-sm font-[degularsemibold] ">Last 7 days</p>
            </button>
            <button className="px-4 py-2.5 border border-gray-100 items-center rounded-[100px]">
              <p className="text-neutral-900 text-sm font-[degularsemibold] ">This month</p>
            </button>
            <button className="px-4 py-2.5 border border-gray-100 items-center rounded-[100px]">
              <p className="text-neutral-900 text-sm font-[degularsemibold] ">Last 3 months</p>
            </button>
            <button className="px-4 py-2.5 border border-gray-100 items-center rounded-[100px]">
              <p className="text-neutral-900 text-sm font-[degularsemibold] ">This year</p>
            </button>
            <button className="px-4 py-2.5 border border-gray-100 items-center rounded-[100px]">
              <p className="text-neutral-900 text-sm font-[degularsemibold] ">Last year</p>
            </button>
            <button className="px-4 py-2.5 border border-gray-100 items-center rounded-[100px]">
              <p className="text-neutral-900 text-sm font-[degularsemibold] ">All time</p>
            </button>
          </div>
          {/* The rest of your filter options */}
        </div>
      </div>
    </div>
  );
};

export default FilterModal;
