import React, { useState } from "react";

const FilterDropdown = () => {
  const [items, setItems] = useState([
    { id: 1, text: "Store Transactions", checked: false },
    { id: 2, text: "Get Tipped", checked: false },
    { id: 3, text: "Withdrawals", checked: false },
    { id: 4, text: "Chargebacks", checked: false },
    { id: 5, text: "Cashbacks", checked: false },
    { id: 6, text: "Refer & Earn", checked: false },
  ]);

  const handleItemClick = (id: number) => {
    const updatedItems = items.map((item) => (item.id === id ? { ...item, checked: !item.checked } : item));
    setItems(updatedItems);
  };

  const [dropdownVisible, setDropdownVisible] = useState(false);

  const selectedItems = items.filter((item) => item.checked);

  return (
    <div className="relative w-full inline-block text-left">
<button
  onClick={() => setDropdownVisible(!dropdownVisible)}
  className="h-12 bg-white rounded-md px-4 py-2 text-sm border-neutral-900 border-2 font-[degularsemibold] items-center justify-text-center w-full"
>
  {selectedItems.length === 0
    ? "Select Transaction Type"
    : selectedItems.map((item) => item.text).join(", ")}
</button>
      {dropdownVisible && (
        <div className="absolute left-0 w-full bg-white border border-gray-200 rounded-md shadow-lg">
          <ul className="py-2">
            {items.map((item) => (
              <li key={item.id} className="flex items-center px-4 py-2 cursor-pointer text-base font-[degularsemibold]">
                <input type="checkbox" className="mr-2 h-4 w-4 text-neutral-900 " checked={item.checked} onChange={() => handleItemClick(item.id)} />
                <span>{item.text}</span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default FilterDropdown;
