import React from "react";

interface FilterModalProps {
  isOpen: boolean;
  onClose: () => void;
}

const FilterModal: React.FC<FilterModalProps> = ({ isOpen, onClose }) => {
  return (
    <div className={`fixed inset-0 flex items-center justify-center ${isOpen ? "" : "hidden"}`}>
      {/* Grayed-out background */}
      <div className="fixed inset-0 bg-gray-400 opacity-50 z-10" onClick={onClose}></div>

      {/* Filter modal */}
      <div className={`fixed top-2 right-0 w-[420px] h-[670px] rounded-md bg-white border border-gray-200 shadow-2xl transform translate-x-0 z-20 transition-transform duration-700 ease-in-out ${isOpen ? "" : "translate-x-full"}`}>
        <div className="p-4">
          {/* Your filter options and other content */}
          <button onClick={onClose}>Close</button>
        </div>
      </div>
    </div>
  );
};

export default FilterModal;
