import React, { useState, useRef } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import FileBase64 from 'react-file-base64';

const Transactions = () => {
  const [userId, setUserId] = useState<string>('');
  const [description, setDescription] = useState<string>('');
  const [videoData, setVideoData] = useState<File | null>(null);
  const [uploadResponse, setUploadResponse] = useState<any | null>(null);
  const [detailsResponse, setDetailsResponse] = useState<any | null>(null);
  const [loading, setLoading] = useState(false);
  const [loadingMessage, setLoadingMessage] = useState<string | null>(null);
  const [requestBody, setRequestBody] = useState({ visibility: '', description: '' });
  const [phone, setPhone] = useState<string>('');
  const [responseText, setResponseText] = useState<string>('');
  const [token, setToken] = useState<string>('');

  const videoRef = useRef<HTMLVideoElement>(null);

  const handlePhoneChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setPhone(event.target.value);
  };

  const handleCheckPhone = async () => {
    if (!phone) {
      toast.error('Please enter a phone number');
      return;
    }

    setLoading(true);

    try {
      const apiUrl = 'https://api.tezda.tech/auth/t/checkPhone';
      const apiKey = 'SytJWAJ6YB7BoiXOwu2Zp5rMLaIGD1at1EqNvnMT';

      const response = await axios.post(
        apiUrl,
        { phone },
        {
          headers: {
            'x-api-key': apiKey,
          },
        }
      );

      const { data } = response.data;
      setResponseText(JSON.stringify(data, null, 2));
      setToken(data.token);
      setUserId(data.user_id);
      toast.success('Phone number checked successfully');
    } catch (error) {
      console.error('Error checking phone number:', error);
      toast.error('Failed to check phone number');
    } finally {
      setLoading(false);
    }
  };

  const handleDescriptionChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setDescription(event.target.value);
    setRequestBody({ ...requestBody, description: event.target.value });
  };

  const getItemIdFromPresignedUrl = (presignedUrl: string) => {
    try {
      const url = new URL(presignedUrl);
      const path = url.pathname;
      const pathParts = path.split('/');
      const fileName = pathParts[pathParts.length - 1];
      // Remove the file extension, assuming it's always ".mp4"
      const itemId = fileName.replace(/\.mp4$/, '');
      return itemId;
    } catch (error) {
      console.error('Error extracting item ID from presigned URL:', error);
      return null;
    }
  };

  const handleVideoUpload = (file: { file: File }) => {
    setVideoData(file.file);
  };

  const handleUpload = async () => {
    if (!userId || !videoData) {
      toast.error('Please enter user ID and select a video');
      return;
    }

    const presignedUrlEndpoint = `
    https://api.tezda.tech/video/t//createUserUploadPresignedUrl?user_id=${userId}`;

    try {
      setLoading(true);

      // Step 1: Request a presigned URL
      const presignedUrlResponse = await axios.post(
        presignedUrlEndpoint,
        { description },
        {
          headers: {
            'x-api-key': 'fwKwNZDxCRJpMWkLXetP1UQHnuUNqj65bCVXe159',
          },
        }
      );
      console.log('Response from presignedUrl request:', presignedUrlResponse);
      const user_id = presignedUrlResponse.data.user_id;
      const presignedUrl = presignedUrlResponse.data.data;

      // Step 2: Upload the video to S3 using the presigned URL
      console.log('Uploading video data:', videoData);
      await axios.put(presignedUrl, videoData, {
        headers: {
          'Content-Type': 'video/mp4',
          'Access-Control-Allow-Origin': '*',
        },
      });

      const item_id = getItemIdFromPresignedUrl(presignedUrl);

      // Step 3: After a successful upload, you can now trigger other actions or display a success message.
      setUploadResponse({ message: 'Video uploaded successfully', data: { presignedUrl, item_id, user_id } });
      toast.success('Video uploaded successfully!');
    } catch (error: any) {
      console.error('Error during upload:', error);

      if (error.response) {
        toast.error(`Error during upload: ${error.response.data.message}`);
      } else {
        toast.error('Unexpected error uploading video. Please try again later.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleGetDetails = async () => {
    if (!uploadResponse) {
      toast.error('Please upload a video first');
      return;
    }

    try {
      setLoadingMessage('Loading video details...');
      setLoading(true);
      toast.info('Loading video details...');

      const detailsEndpoint = `https://api.tezda.tech/auth/t/findUserShortVideo`;
      const detailsResponse = await axios.post(
        detailsEndpoint,
        {
          item_id: uploadResponse.data.item_id,
          user_id: uploadResponse.data.user_id,
        },
        {
          headers: {
            'x-api-key': 'IRlphpPcbk3D81VSuMOcg5851XlQyLZ98dDaY3oB',
            token: token,
          },
        }
      );
      console.log('Response from get details endpoint:', detailsResponse);

      setDetailsResponse(detailsResponse.data);

      if (videoRef.current) {
        const videoUrl = detailsResponse.data.data.videoS3Url;
        videoRef.current.src = videoUrl;
        videoRef.current.load();
        videoRef.current.play();
      }

      toast.success('Video details loaded successfully!');
    } catch (error) {
      console.error('Error getting video details:', error);
      setLoadingMessage('Error getting video details. Please try again later.');
    } finally {
      setLoading(false);
      setLoadingMessage(null);
    }
  };

  return (
    <div className='flex flex-col md:pt-15 pt-5'>
      <div className='flex flex-col md:flex-row justify-between gap-4'>
        <div className='text-neutral-900 flex flex-col gap-4 md:flex'>
          <div>
            <p className='font-[degularbold] md:text-2xl text-xl'>Upload Video Logic</p>
            <p className='font-[degularmedium] text-gray-600'>
              To uplod a video and stream you first login with your phone number. The token and cookies for that user is stored and used to upload the
              vide and view the video respectively. <br /> Enter the video details - visibilty and description. The other parameters for a video are
              added in the background. <br />
            </p>
          </div>
          <div className='flex gap-2 justify-between'>
            <div className='flex flex-col gap-1 w-25'>
              <div>
                <label htmlFor='phone' className='font-semibold text-lg'>
                  Enter Phone Number:
                </label>
                <input
                  id='phone'
                  type='text'
                  value={phone}
                  onChange={handlePhoneChange}
                  placeholder='Enter phone number'
                  className='bg-gray-100 text-gray-900 p-2 rounded-md mt-2 w-full focus:outline-none focus:ring focus:border-blue-500'
                />
                <button
                  onClick={handleCheckPhone}
                  className='bg-blue-500 text-white p-2 rounded-md mt-2 hover:bg-blue-700 focus:outline-none focus:ring focus:border-blue-500 w-full'
                  disabled={loading}
                >
                  {loading ? 'Checking...' : 'Check Phone'}
                </button>
              </div>
              <div className='mt-4 w-25'>
                <h2 className='text-lg font-semibold mb-2'>Response:</h2>
                <pre className='bg-gray-100 p-4 text-sm rounded-md w-[350px] overflow-auto h-40'>{responseText}</pre>
              </div>
              <ToastContainer />
            </div>
            <div className='flex flex-col gap-1 w-full'>
              <div>
                <label htmlFor='video-details' className='font-semibold text-lg'>
                  Video Details:
                </label>
                <input
                  id='video-details'
                  type='text'
                  value={description} // Assuming this should be replaced with appropriate state variable
                  onChange={handleDescriptionChange}
                  placeholder='Enter description'
                  className='bg-gray-100 text-gray-900 p-2 rounded-md mt-2 w-full focus:outline-none focus:ring focus:border-blue-500'
                />
              </div>
              <div className='relative'>
                <select
                  id='visibility'
                  className='bg-gray-100 text-gray-900 p-2 rounded-md mt-2 w-full focus:outline-none focus:ring focus:border-blue-500'
                >
                  <option value='public'>Public</option>
                  <option value='private'>Private</option>
                  <option value='personal'>Personal</option>
                </select>
                <div className='pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-gray-700'>
                  <svg className='w-4 h-4 fill-current' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 20 20' fill='currentColor'>
                    <path
                      fillRule='evenodd'
                      d='M7.293 11.293a1 1 0 0 1 1.414 0L10 12.586l1.293-1.293a1 1 0 1 1 1.414 1.414l-2 2a1 1 0 0 1-1.414 0l-2-2a1 1 0 0 1 0-1.414 1 1 0 0 1 0-1.414z'
                    />
                  </svg>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className='flex flex-col gap-2 md:w-1/2'>
          <div className='mt-4'>
            <p className='font-[degularsemibold] text-xl'>Select Video</p>
            <div className='w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring focus:border-blue-500'>
              <FileBase64 multiple={false} onDone={handleVideoUpload} />
            </div>
          </div>
          <button
            onClick={handleUpload}
            className='w-full bg-blue-500 text-white p-2 rounded-md hover:bg-blue-700 focus:outline-none focus:ring focus:border-blue-500'
          >
            Upload Video
          </button>
          <button
            onClick={handleGetDetails}
            className='w-full bg-green-500 text-white p-2 rounded-md mt-4 hover:bg-blue-700 focus:outline-none focus:ring focus:border-blue-500'
          >
            Get Details
          </button>
        </div>
      </div>
      <div className='w-full border-t mt-4 border-gray-300' />

      {loading && (
        <div className='mt-4 flex justify-center'>
          <p className='text-lg font-bold mb-2'>{loadingMessage || 'Loading...'}</p>
        </div>
      )}

      {uploadResponse && (
        <div className='mt-4 '>
          <h2 className='text-lg font-bold mb-2'>Upload Response:</h2>
          <div>{/* Add any additional elements or styling here if needed */}</div>
          <pre className='bg-gray-100 overflow-auto p-4 text-sm rounded-md'>{JSON.stringify(uploadResponse, null, 2)}</pre>
        </div>
      )}

      {detailsResponse && detailsResponse.data && (
        <div className='mt-4 '>
          <h2 className='text-lg font-bold mb-2'>Full Video Details:</h2>
          <div>{/* Add any additional elements or styling here if needed */}</div>
          <pre className='bg-gray-100 overflow-auto p-4 text-sm rounded-md'>{JSON.stringify(detailsResponse, null, 2)}</pre>
        </div>
      )}

      <div className='mt-4'>
        <h2 className='text-lg font-bold mb-2'>Original Uploaded Video:</h2>
        <video ref={videoRef} controls width='100%' height='auto'>
          Your browser does not support the video tag.
        </video>
      </div>

      <ToastContainer />
    </div>
  );
};

export default Transactions;
